example.code:
sshExec {
    name = "My SSH Exec build step"
    commands = """
        command1
        command2
    """.trimIndent()
    targetUrl = "8.8.8.8"
    authMethod = defaultPrivateKey {
        username = "username"
        passphrase = "credentialsJSON:******"
    }
}

sshExec {
    name = "My SSH Exec build step"
    commands = """
        command1
        command2
    """.trimIndent()
    targetUrl = "8.8.8.8"
    authMethod = password {
        username = "username"
        password = "credentialsJSON:******"
    }
}

sshExec {
    name = "My SSH Exec build step"
    executionMode = BuildStep.ExecutionMode.RUN_ON_FAILURE
    commands = """
        command1
        command2
    """.trimIndent()
    targetUrl = "8.8.8.8"
    authMethod = sshAgent {
        username = "username"
    }
}
